package cavenightingale.offlinecmd;

import com.mojang.authlib.GameProfile;
import net.minecraft.server.v1_12_R1.*;
import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandException;
import org.bukkit.command.CommandSender;
import org.bukkit.craftbukkit.v1_12_R1.CraftServer;
import org.bukkit.plugin.java.JavaPlugin;

public final class Offlinecmd extends JavaPlugin {

        public class NPCConnection extends PlayerConnection{

                public NPCConnection(MinecraftServer minecraftserver, NetworkManager networkmanager, EntityPlayer entityplayer) {
                        super(minecraftserver, networkmanager, entityplayer);
                }
        }

        public class EntityNPC extends EntityPlayer{

                public EntityNPC(MinecraftServer minecraftserver, WorldServer worldserver, GameProfile gameprofile, PlayerInteractManager playerinteractmanager) {
                        super(minecraftserver, worldserver, gameprofile, playerinteractmanager);
                }

                private CommandSender redirect=null;

                public void setRedirect(CommandSender sender){
                        redirect=sender;
                }

                @Override
                public void sendMessage(IChatBaseComponent ichatbasecomponent) {
                        if(null==redirect)
                                super.sendMessage(ichatbasecomponent);
                        else
                                redirect.sendMessage(ichatbasecomponent.toPlainText());
                }
        }

        @Override
        public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
                if(label.equals("offline")) {
                        MinecraftServer server = ((CraftServer) Bukkit.getServer()).getServer();
                        PlayerList manager = server.getPlayerList();
                        EntityNPC npc = new EntityNPC(server, server.getWorldServer(0),
                                        server.getUserCache().getProfile(args[0]),
                                        new PlayerInteractManager(server.getWorldServer(0)));
                        boolean joined=false;
                        try {
                                npc.setRedirect(sender);
                                npc.a(server.getPlayerList().playerFileData.load(npc));
                                manager.onPlayerJoin(npc, "");
                                joined=true;
                                StringBuilder builder = new StringBuilder(args[1]);
                                for (int i = 2; i < args.length; i++) {
                                        builder.append(" ");
                                        builder.append(args[i]);
                                }
                                System.out.println(builder);
                                npc.getBukkitEntity().performCommand(builder.toString());
                        }catch(Throwable ex){
                                throw new CommandException("Unexcepted",ex);
                        }finally {
                                if(joined)
                                        manager.disconnect(npc);
                        }
                        return true;
                }
                return false;
        }
}
