import mods.pyrotech.Bloomery;
import crafttweaker.item.IItemStack;

function bloom(recipe_name as string, output as IItemStack, input as IItemStack, 
                anvil as string[], burntime as int, failurechance as float, 
                yieldmin as int, yieldmax as int, slagitem as IItemStack, slagnum as int,
                failureitem as IItemStack, failurenum as int){
                   Bloomery.createBloomeryBuilder(recipe_name, output,input)
                   .setAnvilTiers(anvil)
                   .setBurnTimeTicks(burntime)
                   .setFailureChance(failurechance)
                   .setBloomYield(yieldmin, yieldmax)
                   .setSlagItem(slagitem, slagnum)
                   .addFailureItem(failureitem, failurenum)
                   .register();
               }

function bloom("bloom_from_ironwood", <twilightforest:ironwood_ingot>, <twilightforest:block_storage:0>, 
              ["granite", "ironclad"], 6000, 0.1, 
              12, 15, <pyrotech:generated_slag_gold>, 2,
              <pyrotech:slag>, 1);
