package com.bilibili.one.procedure;

import net.minecraft.util.DamageSource;
import net.minecraft.entity.EntityLivingBase;
import net.minecraft.entity.Entity;

import com.bilibili.one.ElementsOne;

@ElementsOne.ModElement.Tag
public class ProcedureShenZhiJianMobIsHitWithTool extends ElementsOne.ModElement {
        public ProcedureShenZhiJianMobIsHitWithTool(ElementsOne instance) {
                super(instance, 7);
        }

        public static void executeProcedure(java.util.HashMap<String, Object> dependencies) {
                if (dependencies.get("entity") == null) {
                        System.err.println("Failed to load dependency entity for procedure ShenZhiJianMobIsHitWithTool!");
                        return;
                }
                Entity entity = (Entity) dependencies.get("entity");
                entity.attackEntityFrom(DamageSource.GENERIC, (float) 1e+285);
                if (entity instanceof EntityLivingBase)
                        ((EntityLivingBase) entity).setHealth((float) 0);
        }
}

