              "start": 35.0,
              "stop": 0.0,
              "finalCoefficient": 0.1
            }
          ]
        }
      }
    ]
  },

  "customTerrain": {
    "noiseLayers": [
      {
        "xDensity": 100.0,
        "yDensity": 100.0,
        "multiplier": {
          "baseMultiplier": 100.0,
          "biomeModifier": 0.0,
          "riverModifier": 0.0,
