// 延迟多少tick执行指令
var delay = 60;
// 执行什么指令 这里你可以把~ ~10 ~换成具体坐标
var command = 'tp @a[r=10] ~ ~10 ~';

function died(e){
    e.npc.executeCommand('say npc死了');
    e.npc.timers.forceStart(419,delay,false);
}

function timer(e){
    if(e.id == 419){
        e.npc.executeCommand(command);
    }
}
