public class ClearUtil {
    public static int clearItem(){
        int i = 0;
        for(World world: Bukkit.getWorlds()){
            i+=clearItem(world);
        }
        return i;
    }
    public static int clearItem(World w){
        int i = 0;
        for(Entity en : w.getEntities()){
            if(en.getType()==EntityType.DROPPED_ITEM){
                en.remove();
                i++;
                continue;
            }
        }
        return i;
    }

