package cf.huzpsb.oa;

import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.AsyncPlayerPreLoginEvent;
import org.bukkit.plugin.java.JavaPlugin;

public class Main extends JavaPlugin implements Listener {
    @Override
    public void onEnable() {
        Bukkit.getServer().getPluginManager().registerEvents(this, this);
        getServer().getConsoleSender().sendMessage(ChatColor.GREEN + "云黑系统就绪!");
    }
    @Override
    public void onDisable() {
        getServer().getConsoleSender().sendMessage(ChatColor.RED + "Huzpsb's 外置登录 已关闭.");
    }

    public String ReadWeb(String p) throws Exception {
        HttpURLConnection Web = (HttpURLConnection) new URL(p).openConnection();
        Web.setConnectTimeout(1000);
        Web.setRequestMethod("GET");
        InputStream inStream = Web.getInputStream();
        StringBuilder builder = new StringBuilder(64);
        while (true) {
            int byteRead = inStream.read();
            if (byteRead == -1) {
                return builder.toString();
            }
            builder.append((char) byteRead);
        }
    }

    @EventHandler
    public void Login(AsyncPlayerPreLoginEvent a) throws Exception {
        if (!ReadWeb("http://cloudban.gear.host/player.php?info="+a.getName()).contains("-1")) {
            a.setLoginResult(AsyncPlayerPreLoginEvent.Result.KICK_BANNED);
            a.setKickMessage("java.ioException.Null");
            return;
        }
        getServer().getConsoleSender().sendMessage(ChatColor.GREEN + a.getName() + "已登录.");
    }
}
