    {
      "type": "toolTip",
      "entries": [
        {
          "item": "minecraft:coal",  #物品ID
          "text": ["Hold SHIFT for more"],  #文本内容
          "mode": "noShift"  #模式
        },
        {
          "item": "minecraft:coal",  #物品ID
          "text": ["This is just coal.", "Or not?"], #文本内容
          "mode": "shift"  #模式
        }
      ]
    },





