package io.github.euonmyoji.yysmending;

import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.enchantments.Enchantment;
import org.bukkit.entity.ExperienceOrb;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerExpChangeEvent;
import org.bukkit.event.player.PlayerItemMendEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.PlayerInventory;
import org.bukkit.inventory.meta.Damageable;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.plugin.java.JavaPlugin;

import java.util.List;
import java.util.Random;
import java.util.stream.Collectors;
import java.util.stream.Stream;

/**
 * @author yinyangshi
 */
public class YYSMending extends JavaPlugin implements Listener {
    private static final Random RANDOM = new Random();

    @EventHandler
    public void onExpChange(PlayerExpChangeEvent event) {
        int amount = event.getAmount();
        if (amount > 0 && event.getSource() instanceof ExperienceOrb) {
            ExperienceOrb exp = ((ExperienceOrb) event.getSource());
            Player p = event.getPlayer();
            PlayerInventory inventory = p.getInventory();
            List<ItemStack> list = Stream.concat(Stream.of(inventory.getItemInMainHand(), inventory.getItemInOffHand()),
                    Stream.of(inventory.getArmorContents()))
                    .filter(itemStack -> itemStack != null && itemStack.getType() != Material.AIR)
                    .filter(itemStack -> itemStack.hasItemMeta() && itemStack.getItemMeta() instanceof Damageable)
                    .filter(itemStack -> ((Damageable) itemStack.getItemMeta()).getDamage() > 0
                            && itemStack.getEnchantments().containsKey(Enchantment.MENDING))
                    .collect(Collectors.toList());
            while (list.size() > 0 && amount > 0) {
                int index = RANDOM.nextInt(list.size());
                ItemStack item = list.get(index);
                ItemMeta meta = item.getItemMeta();
                int repairAmount = 2;
                int damage = ((Damageable) meta).getDamage() - 2;
                if (damage < 0) {
                    repairAmount = ((Damageable) meta).getDamage();
                    list.remove(index);
                }
                PlayerItemMendEvent mendEvent = new PlayerItemMendEvent(p, item, exp, repairAmount);
                Bukkit.getPluginManager().callEvent(mendEvent);
                if (mendEvent.isCancelled()) {
                    list.remove(index);
                } else {
                    ((Damageable) meta).setDamage(((Damageable) meta).getDamage() - mendEvent.getRepairAmount());
                    item.setItemMeta(meta);
                    amount -= 1;
                }
            }
            event.setAmount(amount);
        }
    }

    @Override
    public void onEnable() {
        Bukkit.getPluginManager().registerEvents(this, this);
    }
}

