                "up":    { "uv": [  7, 0,  9,  7 ], "texture": "#edge" },
                "north": { "uv": [  7, 0,  9, 16 ], "texture": "#edge", "cullface": "north" },
                "west":  { "uv": [ 16, 0,  9, 16 ], "texture": "#pane" },
                "east":  { "uv": [  9, 0, 16, 16 ], "texture": "#pane" }
            }
        }
    ]
}
