package main

import (
        "bufio"
        "fmt"
        "io"
        "os"
        "strings"
)

func main() {
        f, err := os.Open(`1.html`)
        if err != nil {
                panic(err)
        }
        b := bufio.NewReader(f)
        var i int
        for {
                line, err := b.ReadString('\n')
                if err != nil {
                        if err == io.EOF {
                                break
                        } else {
                                panic(err)
                        }
                }
                i = i + strings.Count(line, `🐶`)
        }
        fmt.Println(i)
}
