开源这东西就不管啥协议了,看bbs没人发这东西,就发出来分享了
package cn.loliidc.ae2fix;

import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryOpenEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.plugin.java.JavaPlugin;

public class Main extends JavaPlugin
        implements Listener {
    @Override
    public void onEnable() {
        getLogger().info(" /* AecraftFix > ");
        getLogger().info("  - 仅仅只是不让玩家手持物品打开合成终端");
        getLogger().info("  - MCBBS ID: @MoeVien .");
        getLogger().info(" */");
        Bukkit.getPluginManager().registerEvents(this, this);
    }

    @EventHandler
    public void onOpen(InventoryOpenEvent event){
        String name = event.getInventory().getName();
        if (name.equals("AEMultiPart")) {
            Player player = (Player) event.getPlayer();
            final ItemStack item = player.getItemInHand();
            if (item != null && item.getType() != Material.AIR) {
                event.setCancelled(true);
                player.sendMessage("§c§lAe2craftFix §f// §7请确定手上无任何物品再打开合成终端!");
            }
        }
    }

    @Override
    public void onDisable() {
        super.onDisable();
        getLogger().info(" /* AecraftFix > 卸载完毕 */");
    }
}
