开源：


#include <iostream>
#include <Windows.h>

using namespace std;

int main()
{
        unsigned int reply{ 0 };//回复数
        unsigned int look_over{ 0 };//查看数
        unsigned int price{ 0 };//水值

        cout.precision(1);
        cout.flags(cout.fixed);

        for(;;)
        {
                reply = 0;
                look_over = 0;

                cout << "欢迎使用水值计算器" << endl;
                cout << "水值计算器是通过“一个帖子回复数^2/查看数”计算水值的" << endl;
                cout << "得出来的值越高，帖子就越不水" << endl;

                cout << "请输入帖子的回复数：" << endl;
                cin >> reply;
                cout << "请输入帖子的查看数：" << endl;
                cin >> look_over;

                price = reply * reply / look_over;

                cout << "这个帖子的水值大约是：" << price << endl;

                system("pause");
                system("cls");
        }
}复制代码

