Player player = (Player) sender;
ArmorStand last = null;
for (int a = 0; a<=100;a++) {
    ArmorStand llast = last;
    last = player.getWorld().spawn(player.getLocation(), ArmorStand.class);
    last.setMarker(true);
    last.setVisible(false);
    last.setCustomName(a + player.getName());
    last.setCustomNameVisible(true);
    last.setSmall(true);
    last.setPassenger(llast);
}
player.setPassenger(last);
