public void registerIcons(IconRegister iconRegister){
this.icon = iconRegister.registerIcon("mod_name:block_name"); 
//你需要在 源码目录/mods/textures/blocks/ 目录下放置block_name.png 文件
//另外mod_name是你的源码文件夹目录名
}

// 使用
public Icon getBlockTextureFromSideAndMetadata(int side, int metadata) {
return this.icon
}

