package com.hotmail.kwolsink.dailynews;

import java.util.logging.Logger;
import org.bukkit.*;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.entity.Player;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.PlayerInventory;
import org.bukkit.plugin.java.JavaPlugin;

public class Dailynews extends JavaPlugin
{

        public Dailynews()
        {
        }

        public void onEnable()
        {
                getLogger().info("Dailynews by kaskoepoes112!");
                saveConfig();
        }

        public void onDisable()
        {
                saveConfig();
        }

        public boolean onCommand(CommandSender sender, Command cmd, String label, String args[])
        {
                if (label.equalsIgnoreCase("release"))
                {
                        Player p = (Player)sender;
                        if (!p.hasPermission("dailynews.release"))
                        {
                                p.sendMessage((new StringBuilder()).append(ChatColor.RED).append("You don't have the permissions to do this!").toString());
                                return true;
                        }
                        ItemStack i = p.getItemInHand();
                        if (i.getType().equals(Material.WRITTEN_BOOK))
                        {
                                Player aplayer[];
                                int k = (aplayer = Bukkit.getOnlinePlayers()).length;
                                for (int j = 0; j < k; j++)
                                {
                                        Player player = aplayer[j];
                                        if (!p.equals(player))
                                        {
                                                player.getInventory().addItem(new ItemStack[] {
                                                        i
                                                });
                                                player.sendMessage((new StringBuilder()).append(ChatColor.GOLD).append(getConfig().getString("news.message")).toString());
                                                player.updateInventory();
                                        }
                                }

                                p.sendMessage((new StringBuilder()).append(ChatColor.GOLD).append("The news is published!").toString());
                                return true;
                        }
                        p.sendMessage((new StringBuilder()).append(ChatColor.RED).append("You can only release written books!").toString());
                }
                return true;
        }
}复制代码
