C#的代码 读JSON的 你可以看下
        private void runGame()
        {
            //读配置文件
            List<string> list = new List<string>();
            FileStream fs = new FileStream(Directory.GetCurrentDirectory() + @"\setting.cfg", FileMode.OpenOrCreate);
            StreamReader sr = new StreamReader(fs);
            while (!sr.EndOfStream)
            {
                string curstr = sr.ReadLine();
                list.Add(curstr);
            }
            sr.Close();
            for (int i = 0; i < 3; i++)
            {
                int index = list[i].IndexOf('=');
                list[i] = list[i].Substring(index + 1, list[i].Length - index - 1);
            }
            useName = list[0];
            javaPath = list[1];
            RAM = list[2];

            string rl = "";
            int j = 0;
            List<string> list1 = new List<string>();
            FileStream fs1 = new FileStream(Directory.GetCurrentDirectory() + @"\\.minecraft\versions"+cbList.Text+'\\'+cbList.Text+".json", FileMode.OpenOrCreate);
            StreamReader sr1 = new StreamReader(fs1);
            while (!sr1.EndOfStream)
            {
                string curstr = sr1.ReadLine();
                list1.Add(curstr);
            }
            sr1.Close();

            for (int i = 0; i < list1.Count; i++)
            {
                if (list1[i].Length > 13)
                {
                    int index = list1[i].IndexOf(@"""name"": """);

                    if (index != -1 && list1[i].IndexOf("osx") < 0)
                    {
                        list1[j] = list1[i].Substring(index + 9, list1[i].Length - index - 9);
                        list1[j] = list1[j].Replace(""", "");
                        list1[j] = list1[j].Replace(",", "");
                        list1[j] = list1[j].Trim();
                        j++;
                    }
                }
            }
            for (int i = 0; i < j - 4; i++)
            {
                int a = list1[i].IndexOf('.');
                int b = list1[i].IndexOf(':');
                while (a < b && a > 0)
                {
                    list1[i] = list1[i].Remove(a, 1);
                    list1[i] = list1[i].Insert(a, "\");
                    a = list1[i].IndexOf('.');
                    b = list1[i].IndexOf(':');
                }
                int index = list1[i].IndexOf(':');
                list1[i] = list1[i] + '\\' + list1[i].Substring(index + 1, list1[i].Length - index - 1) + ".jar";
                list1[i] = list1[i].Replace(":", "\");
                int lastindex = list1[i].LastIndexOf('\\');
                list1[i] = list1[i].Remove(lastindex, 1);
                list1[i] = list1[i].Insert(lastindex, "-");
                list1[i] = Directory.GetCurrentDirectory() + @"\.minecraft\libraries" + list1[i];
                rl = rl + list1[i] + ";" ;
            }

            string runLine = "-Xmx" + RAM + "m -Dfml.ignoreInvalidMinecraftCertificates=true -Dfml.ignorePatchDiscrepancies=true -Djava.library.path=" + @".minecraft\natives" + @" -cp " + '"' +rl + Directory.GetCurrentDirectory() + @"\.minecraft\versions" + cbList.Text + '\\' + cbList.Text + ".jar" + '"' + " net.minecraft.launchwrapper.Launch --username " + useName + " --session ${auth_session} --version " + cbList.Text + @" --gameDir .minecraft --assetsDir .minecraft\assets --tweakClass cpw.mods.fml.common.launcher.FMLTweaker";


            System.Diagnostics.Process process = new System.Diagnostics.Process();
            process.StartInfo.FileName = javaPath;
            process.StartInfo.Arguments = runLine;
            process.Start();
            this.Close();
        }
    }复制代码






