package SAOCMT;

import net.minecraft.block.Block;
import SAOCMT.blocks.block_caihong;
import SAOCMT.proxy.Proxy;
import cpw.mods.fml.common.Mod;
import cpw.mods.fml.common.Mod.EventHandler;
import cpw.mods.fml.common.Mod.Init;
import cpw.mods.fml.common.Mod.Instance;
import cpw.mods.fml.common.SidedProxy;
import cpw.mods.fml.common.event.FMLInitializationEvent;
import cpw.mods.fml.common.network.NetworkMod;
import cpw.mods.fml.common.registry.GameRegistry;
import cpw.mods.fml.common.registry.LanguageRegistry;
import net.minecraft.block.Block;
import net.minecraft.src.BaseMod;
import net.minecraft.src.ModLoader;

@Mod(modid = "SAOCMT", name = "SAOCMT" , version = "1.1.1.1")
@NetworkMod(clientSideRequired = true, serverSideRequired = false)
public class saocptmod {
    @Instance("SAOCMT") 
    public static saocptmod instance;
    
    
    @SidedProxy(clientSide = "SAOCMT.proxy.ClientProxy", serverSide = "SAOCMT.proxy.Proxy")
    public static Proxy proxy;
    
    @EventHandler
    public void init(FMLInitializationEvent event){
        proxy.init(); 
        Block block_caihong = (new block_caihong(77001)).setUnlocalizedName("block_caihong");
        
  LanguageRegistry.addName(block_caihong, "彩虹方块");
  GameRegistry.registerBlock(block_caihong,"block_caihong");
    }
    }
    



