private void onFall(EntityDamageEvent e)
  {
    if ((this.plugin.getConfig().getBoolean("配置表中是否开启免除伤害") == Boolean.TRUE.booleanValue()) && 
      (e.getCause() == EntityDamageEvent.DamageCause.FALL))
    {
      Entity ee = e.getEntity();

      if ((ee instanceof Player))
      {
        Player p = (Player)ee;
        p.setHealth(20.0D);
        e.setCancelled(true);
      }
    }
  }复制代码
