package me.Fear.toturial;

import org.bukkit.Bukkit;
import org.bukkit.Location;
import org.bukkit.entity.Entity;
import org.bukkit.entity.Cow;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.entity.EntityDamageEvent;
import org.bukkit.plugin.java.JavaPlugin;

import de.kumpelblase2.remoteentities.EntityManager;
import de.kumpelblase2.remoteentities.RemoteEntities;
import de.kumpelblase2.remoteentities.api.RemoteEntity;
import de.kumpelblase2.remoteentities.api.RemoteEntityType;

public class Tutorial extends JavaPlugin implements Listener {

        public void onEnable() {
                getServer().getPluginManager().registerEvents(this, this);

                for (Entity e : Bukkit.getWorld("world").getEntities()) {
                        if (e instanceof Cow) {
                                e.remove();
                        }
                }

                ourEntities();
        }

        private void ourEntities() {
                Location loc = new Location(Bukkit.getWorld("world"), 0, 50, 0);

                EntityManager manager = RemoteEntities.createManager(this);
                RemoteEntity entity = manager.createEntity(RemoteEntityType.Cow, loc, false);
                entity.setStationary(true);
                entity.setPushable(false);
                entity.getBukkitEntity().setCustomName("§aTESTING");
                entity.getBukkitEntity().setCustomNameVisible(true);
        }

        @EventHandler
        public void onDamage(EntityDamageEvent e) {
                e.setCancelled(true);
        }


