package 第三周编程作业;

import java.util.Scanner;

public class 数字特征值 {

        public static void main(String[] args) {
                // TODO Auto-generated method stub
                Scanner in = new Scanner(System.in);
//                程序初始化
                System.out.println(": 数字特征值 - 奇偶特征值 : 计算器");
                System.out.print("请输入一个非负整数,不大于1000000:");
//                优化用户界面
                int a=0;
                int b=a;
                int count=0;
                int c=0;
                int d=0; 
                int e=0;
                int ce=0;
                int binary=0;
                int count1=0;
                a = in.nextInt();
//                定义变量
                if (a<0)
                {
                        System.out.println("所输入的值不能是负数!程序终止.");
                        System.exit(0);
                }
                else
                {
                        if ( a > 1000000 )
                        {
                                System.out.println("所输入的值不能大于1000000!程序终止.");
                                System.exit(0);
                        }
                }
//                判断输入是否正确
                while ( a > 0 )
                {
                        b = a%10;
                        a = a/10;
                        d = b;
                        count=count+1;
                        /*判断的d(b)值为单双 输出为d*/
                        if(d%2 == 0)
                        {
                                c=0;
                        }
                        else
                        {        if (d == 0)
                                {
                                        c=0;
                                }
                                else
                                {
                                        c=1;
                                }
                        }
                        /*判断累积器的单双 输出为e*/
                        if(count%2 == 0)
                        {
                                e=0;
                        }
                        else
                        {        if (count == 0)
                                {
                                        e=0;
                                }
                                else
                                {
                                        e=1;
                                }
                        }
//                        比较c,e的大小关系 若相等则输出1 若不相等输出0 输出de
                        if (c == e)
                        {
                                ce=1;
                        }
                        else
                        {
                                ce=0;
                        }
                                binary=(int)(Math.pow(2,(count-1)));
        //                        得出二进制值
                        if (ce == 1)
                        {
                                count1=count1+binary;
                        }
                }
                System.out.println("奇偶特征值:");
                System.out.println(count1);
//                输出
                in.close();
        }

}
