package tk.a08381.BoomMinecart;

import java.util.List;
import org.bukkit.Bukkit;
import org.bukkit.World;
import org.bukkit.entity.Entity;
import org.bukkit.entity.minecart.HopperMinecart;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryMoveItemEvent;
import org.bukkit.plugin.java.JavaPlugin;

public class BoomMinecart extends JavaPlugin  {
    
    private static HMBOOM hmboom = new HMBOOM();

    public void onEnable() {
        getServer().getPluginManager().registerEvents(hmboom, this);
        getLogger().info("漏斗矿车爆破工具准备完毕，请指示");
    }

    public void onDisable() {
        InventoryMoveItemEvent.getHandlerList().unregister(hmboom);
        getLogger().info("漏斗矿车爆破工具解除完毕");
    }
    
}

class HMBOOM implements Listener  {
    
    private HopperMinecart entity;

    private void Boom(InventoryMoveItemEvent ev){
        if (ev.getDestination().getHolder() instanceof HopperMinecart) {
            ev.setCancelled(true);
            if (entity != (HopperMinecart) ev.getDestination().getHolder()){
                entity = (HopperMinecart) ev.getDestination().getHolder();
                double x,y,z;
                x=entity.getLocation().getX();
                y=entity.getLocation().getY();
                z=entity.getLocation().getZ();
                World world = entity.getWorld();
                List<Entity> entities = entity.getNearbyEntities(10, 10, 10);
                entity.remove();
                world.createExplosion(x,y,z,8, false,false);
                Bukkit.broadcast(
                        String.format(
                                "§a[爆破小分队]§6成功炸毁一台漏斗矿车于§d【%1$s，%2$d，%3$d，%4$d】",
                                world.getName(),
                                (int) x,
                                (int) y,
                                (int) z
                        ),
                        "BoomMinecart.broadcast"
                );
            }
        }
    }
    
}复制代码

