import java.util.Random;
import cpw.mods.fml.common.IWorldGenerator;
import net.minecraft.block.Block;
import net.minecraft.world.World;
import net.minecraft.world.chunk.IChunkProvider;
import net.minecraft.world.gen.ChunkProviderHell;
import net.minecraft.world.gen.feature.WorldGenMinable;
public class <i>WorldGen</i> implements IWorldGenerator {
        public void generate(Random random, int chunkX, int chunkZ, World world, IChunkProvider chunkGenerator, IChunkProvider chunkProvider) {
                for(int i = 0; i < 6; i++){//通过循环获得稀有度——没有问题的话每个区块会生成6个矿脉
                        int randPosX = chunkX + random.nextInt(16);
                        int randPosY = random.nextInt(24)+8;//定义高度为8~24格
                        int randPosZ = chunkZ + random.nextInt(16);
                        (new WorldGenMinable(<i>blockID</i>, 4)).generate(world, random, randPosX, randPosY, randPosZ);//“4”为数量，一组矿脉最多生成四个矿
                        }
                System.out.println("成功生成矿石！");
                }
}复制代码
