package com.mchxzh;

import java.io.File;
import java.util.logging.Logger;
import org.bukkit.Server;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.event.Listener;
import org.bukkit.plugin.PluginManager;
import org.bukkit.plugin.java.JavaPlugin;

public class LoginMessage extends JavaPlugin
  implements Listener
{
  static String joinmessage1;
  static String joinmessage2;
  static String joinmessage3;
  static String joinmessage4;
  static String joinmessage5;
  static String joinmessage6;
  static String joinmessage7;
  static String joinmessage8;
  static String joinmessage9;
  static String joinmessageop;

  public void onEnable()
  {
    File file = new File(getDataFolder(), "config.yml");
    if (!(file.exists())) {
      saveDefaultConfig();
    }

    joinmessage1 = getConfig().getString("1");
    joinmessage2 = getConfig().getString("2");
    joinmessage3 = getConfig().getString("3");
    joinmessage4 = getConfig().getString("4");
    joinmessage5 = getConfig().getString("5");
    joinmessage6 = getConfig().getString("6");
    joinmessage7 = getConfig().getString("7");
    joinmessage8 = getConfig().getString("8");
    joinmessage9 = getConfig().getString("9");
    joinmessageop = getConfig().getString("OP");
    getServer().getPluginManager().registerEvents(new Listen(), this);
    getLogger().info("==========================");
    getLogger().info("LoginMessage已启用!");
    getLogger().info("作者：sun0825");
    getLogger().info("持续更新，请关注有无最新版");
    getLogger().info("==========================");
  }

  public void onDisable()
  {
    getLogger().info("LoginMessage已停用!");
  }
