题目1

#include<stdio.h>
void test(int a, int b, int c)
{
 if(a > b) 
  test(c, a, b);
 else
  printf("%d,%d,%d\n", a, b, c);
}
int main()
{
 int a, b, c;
 scanf("%d %d %d", &a, &b, &c);
 test(a, b, c);
 return 0;
}
