.mtm {
    /*border:1px solid red;*/
}

.mtm li {
    height: 90px;
    border-bottom: 1px dashed #ababab;
    margin-bottom: 5px;
    float: left;
    margin-right: 10px;
}

.mtm .mg_img {
    height: 100%;
    float: left;
    padding: 0;
    width: 60px;
    background-position: 0 -10px;
    overflow: hidden;
    position: relative;
}

.mtm .mg_img img {
    position: absolute;
    top: -5px;
    left: 50%;
    margin-left: -20px;
    margin-top: 0;
}

.mtm li > p {
    position: relative;
    top: 24.5%;
}

.mtm li > p.xw1 {
    color: #333;
    margin-bottom: 2px;
}
