.mg_img {
    width: 35px;
    height: 55px;
    padding: 0px;
    border: none;
    background: none;
}
li, p {
    list-style: none;
}
p {
    width: auto;
}
.mgcl li {
    margin: 10px;
    border: 5px solid blue;
    border-radius: 7px;
    -webkit-transition: border .20s ease-in-out;
    transition: border .20s ease-in-out;
}
.mgcl li:hover {
