package net.minecraft.src;

 public class mod_Mod名字 extends BaseMod
 {
 public static final Item 东西名字 = new Item(ID).setItemName("随便4个字母");
 public void load()
 {
 东西名字.iconIndex = ModLoader.addOverride("/gui/items.png", "/照片文件夹/照片名字png");

 ModLoader.addName(东西名字, "在游戏里的名字");

 yourItem.setTabToDisplayOn(CreativeTabs.tabMaterials);
 }

 public String getVersion()
 {
 return "1.3.2";
 }
 }复制代码
[lime_wool]新方块  mod_方块
package net.minecraft.src;
public class mod_方块 extends BaseMod
{
public static final Block 方块 = new Block方块名字(160, 0).setBlockName("随便4个字母").setHardness(3F).setResistance(4F).setLightValue(1F).setCreativeTab(CreativeTabs.tabBlock).setStepSound(Block.soundGrassFootstep);
public void load()
{
nameHere.blockIndexInTexture = ModLoader.addOverride("/terrain.png", "/图片文件夹/图片.png");
ModLoader.registerBlock(方块);
ModLoader.addName(方块, "游戏里的名字");
ModLoader.addRecipe(new ItemStack(方块, 1), new Object [] {"#", Character.valueOf('#'), Block.dirt});
}
public String getVersion()
{
return "1.3.2";
}
}复制代码Block方块名字
package net.minecraft.src;
 import java.util.Random;
 public class Block方块名字 extends Block
 {
 public Block方块名字(int i, int j)
 {
 super(i, j, Material.wood);
 }
 public int idDropped(int i, Random random, int j)
 {
 return mod_方块.方块名字.blockID;
 }
 public int quantityDropped(Random random)
 {
 return 1;
 }
 }复制代码
