package io.github.sakuraying.fmltutor.loader;

import io.github.sakuraying.fmltutor.fmltutor;
import io.github.sakuraying.fmltutor.item.ItemGoldenEgg;
import net.minecraft.client.Minecraft;
import net.minecraft.client.resources.model.ModelResourceLocation;
import net.minecraft.item.Item;
import net.minecraftforge.fml.common.event.FMLPreInitializationEvent;
import net.minecraftforge.fml.common.registry.GameRegistry;
import net.minecraftforge.fml.relauncher.Side;
import net.minecraftforge.fml.relauncher.SideOnly;

public class ItemLoader {
    public static Item goldenEgg = new ItemGoldenEgg();
    public ItemLoader(FMLPreInitializationEvent event){
        GameRegistry.registerItem(goldenEgg, "golden_egg", fmltutor.MODID);
    }

    @SideOnly(Side.CLIENT)
    public static void registerRender(Item item , String name){
        Minecraft.getMinecraft().getRenderItem().getItemModelMesher().register(item, 0,
                new ModelResourceLocation(fmltutor.MODID + ":" + name, "inventory"));
    }

    @SideOnly(Side.CLIENT)
    public static void registerRenders(){
        registerRender(goldenEgg, "golden_egg");
    }

}
