package org.wwq.smailAutoMessage;

import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.plugin.java.JavaPlugin;

public class Main extends JavaPlugin implements Listener {
        public void onEnable() {
                System.out.println("[Cafer提示]插件开始了!");
                Bukkit.getPluginManager().registerEvents(this, this);
                this.saveDefaultConfig();
        }

        public void onDisable() {
                System.out.println("[Cafer提示]插件结束了!");
        }

        public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {
                if (cmd.getName().equalsIgnoreCase("cafer")) {
                        sender.sendMessage("Boom! Shakalaka! 你输入了高端大气上档次的玩家名。");
                        return true;
                }
                return false;
        }

        @EventHandler
        public void onPlayerJoin(PlayerJoinEvent evt) {
                Player p = evt.getPlayer();
                p.sendMessage("========欢迎来到服务器========");
                p.sendMessage("名称:"+p.getName());
                p.sendMessage("生命:"+p.getHealth()+"/"+p.getMaxHealth());
                p.sendMessage("等级:"+p.getLevel());
                p.sendMessage("坐标: X:"+p.getLocation().getBlockX()+" Y:"+p.getLocation().getBlockY()+" Z:"+p.getLocation().getBlockZ());
                p.sendMessage("世界:"+p.getWorld().getName());
                p.sendMessage("插件由 Cafer 制作. 版本:0.0.1");
                
                Bukkit.broadcastMessage(p.getName()+this.getConfig().getString("PlayerJoinMessage"));
        }
}

