int ledPin = 13;
int delayTime = 1000;
void setup()
{
pinMode(ledPin,OUTPUT);
}
void loop()
{
digitalWrite(ledPin,HIGH);
delay(delayTime);
digitalWrite(ledPin,LOW);
delay(delayTime);
if(delayTime<100)
{
delayTime=1000;//当延时小于0.1s时校准延时时间为1s??
}
else
{
delayTime=delayTime-100;//大于或等于0.1s时将延时时间缩短
}
}
