package com.example.examplemod.blockAndItem.TileEntity;

import org.lwjgl.opengl.GL11;

import com.example.examplemod.ExampleMod;

import net.minecraft.client.renderer.Tessellator;
import net.minecraft.client.renderer.tileentity.TileEntitySpecialRenderer;
import net.minecraft.tileentity.TileEntity;
import net.minecraft.util.ResourceLocation;

public class Entitydraw extends TileEntitySpecialRenderer{
        public static ResourceLocation b=new ResourceLocation("/diracon/textures/entity/2.png");
        @Override
        public void renderTileEntityAt(TileEntity a, double x, double y, double z,
                        float p_147500_8_) {
        this.bindTexture(b);
        GL11.glDisable(GL11.GL_LIGHTING);
        GL11.glDisable(GL11.GL_CULL_FACE);


        Tessellator tessellator = Tessellator.instance;
        tessellator.startDrawingQuads();

        tessellator.addVertexWithUV(x    , y,     z, 0, 1);
        tessellator.addVertexWithUV(x - 1, y    , z, 1, 1);
        tessellator.addVertexWithUV(x - 1, y + 1, z, 1, 0);
        tessellator.addVertexWithUV(x,     y + 1, z, 0, 0);

        tessellator.draw();

        GL11.glEnable(GL11.GL_LIGHTING);
        GL11.glEnable(GL11.GL_CULL_FACE);

                
        }

}
