package me.kayoz;

import java.util.Random;
import java.util.logging.Logger;
import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.block.Block;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.plugin.PluginDescriptionFile;
import org.bukkit.plugin.java.JavaPlugin;

public class Main
  extends JavaPlugin
{
  public final Logger logger = Logger.getLogger("Minecraft");
  
  public void onEnable()
  {
    PluginDescriptionFile pdfFile = getDescription();
    this.logger.info(pdfFile.getName() + "Version " + pdfFile.getVersion() + " Has been Enabled!");
  }
  
  public void onDisable()
  {
    PluginDescriptionFile pdfFile = getDescription();
    this.logger.info(pdfFile.getName() + "Version " + pdfFile.getVersion() + " Has been Disabled!");
  }
  
  public boolean onCommand(CommandSender sender, Command cmd, String commandLabel, String[] args)
  {
    if (cmd.getName().equalsIgnoreCase("randomtp"))
    {
      if (!sender.hasPermission("randomtp.use"))
      {
        sender.sendMessage(ChatColor.RED + "You are not permitted to use this command!");
        return true;
      }
      Player player = (Player)sender;
      
      Location originalLocation = player.getLocation();
      
      Random random = new Random();
      
      Location teleportLocation = null;
      
      int x = random.nextInt(5000) + 1;
      int y = 150;
      int z = random.nextInt(5000) + 1;
      
      boolean isOnLand = false;
      while (!isOnLand)
      {
        teleportLocation = new Location(player.getWorld(), x, y, z);
        if (teleportLocation.getBlock().getType() != Material.AIR) {
          isOnLand = true;
        } else {
          y--;
        }
      }
      player.teleport(new Location(player.getWorld(), teleportLocation.getX(), teleportLocation.getY() + 1.0D, teleportLocation.getZ()));
      
      player.sendMessage(ChatColor.GREEN + "You have been teleported " + ChatColor.AQUA + (int)teleportLocation.distance(originalLocation) + ChatColor.GREEN + " blocks away!");
      
      return true;
    }
    return true;
  }
}
