大家好我是冰雪奇缘的艾莎 :D
这又是一个轻量但是有趣的插件
这个插件可以让穿着指定的鞋子的玩家踩到水时把水冻成冰!
合成表: 
  玻璃       |  钻石  |  玻璃
  钻石       |钻石鞋 |  钻石

  下界之星 |  水桶  |下界之星



权限:
  ke.is.use (使用冰鞋的权限)


配置:
Enable: True
#是否启用
Radius: 1
#检测半径
ShoesRadius: 1
#冰冻的半径(别作死)

源代码(Java): 
package cn.moecraft.iceshoes;
import java.util.ArrayList;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.World;
import org.bukkit.block.Block;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.EventPriority;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerMoveEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.ShapedRecipe;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.plugin.java.JavaPlugin;
/**
 *
 * [url=home.php?mod=space&uid=1231151]@author[/url] kilpikonna
 */
public class IceShoes
    extends JavaPlugin
    implements Listener
    {
    public static ItemStack imRecipe1;
  public static ArrayList<String> Lore1 = new ArrayList();
  boolean enable;  
  public void onEnable()
  {
    this.enable = getConfig().getBoolean("Enable");
    getServer().getPluginManager().registerEvents(this, this);
    for (World world : Bukkit.getWorlds()) {
      getConfig().addDefault(world.getName() + ".enable", Boolean.valueOf(true));
    }
    getConfig().options().copyDefaults(true);
    saveConfig();
    Lore1.add(ChatColor.AQUA + "冰冻 1");
    Bukkit.getConsoleSender().sendMessage("[KE][冰鞋]加载完成,插件作者:kilpikonna");
   
    /*添加合成表*/
    imRecipe1 = new ItemStack(Material.DIAMOND_BOOTS);
    ItemMeta im = imRecipe1.getItemMeta();
    im.setDisplayName(ChatColor.LIGHT_PURPLE + "冰霜の鞋");
    im.setLore(Lore1);
    imRecipe1.setItemMeta(im);
    ShapedRecipe recipe = new ShapedRecipe(imRecipe1);
    recipe.shape(new String[] { "aba", "bzb", "cdc" });
    recipe.setIngredient('a', Material.GLASS);
    recipe.setIngredient('b', Material.DIAMOND);
    recipe.setIngredient('c', Material.NETHER_STAR);
    recipe.setIngredient('d', Material.WATER_BUCKET);
    recipe.setIngredient('z', Material.DIAMOND_BOOTS);
    Bukkit.getServer().addRecipe(recipe);
  }
  
  public void onDisable()
  {
    Bukkit.getConsoleSender().sendMessage("[KE][冰鞋]卸载完成,插件作者:kilpikonna");
  }
  @EventHandler(priority=EventPriority.NORMAL)
  public void Event(PlayerMoveEvent e)
  {
    Player p = e.getPlayer();
    Location to = e.getTo();
    ItemStack boots = p.getInventory().getBoots();
    int x = to.getBlockX();
    int y = to.getBlockY();
    int z = to.getBlockZ();
    int radius = getConfig().getInt("Radius");
    int bootRadius = getConfig().getInt("ShoesRadius");
    if ((p.hasPermission("ke.is.use")))
    {
        if (p.getInventory().getBoots() != null)
        {
            if ((p.getInventory().getBoots().hasItemMeta()))
            {
                if((p.getInventory().getBoots().getItemMeta().getLore() != null))
                {
                    if(((p.getInventory().getBoots().getItemMeta().getLore().equals(Lore1))))
                    {
                        mainFunc(to, x, y, z, bootRadius);
                    } else {
                    } 
                } else ;
            } else ;
        } else ;
    } else ;
  }
  
  public void mainFunc(Location to, int x, int y, int z, int radius)
  {
    for (int i = -radius; i <= radius; i++) 
    {
        for (int xToReplace = -radius; xToReplace <= radius; xToReplace++) 
        {
            for (int zToReplace = -radius; zToReplace <= radius; zToReplace++)
            {
                Location bLocation = new Location(to.getWorld(), x - xToReplace, y - 1, z - zToReplace);
                Location loc2 = new Location(to.getWorld(), x - xToReplace, y - 2, z - zToReplace);
                Block b = bLocation.getBlock();
                Block b2 = loc2.getBlock();
                if (b.getType().equals(Material.STATIONARY_WATER) || (b.getType().equals(Material.WATER))) 
                {
                    b.setType(Material.ICE);
                } else if ((b.getType().equals(Material.AIR)) && (b2.getType().equals(Material.STATIONARY_WATER))) {
                    b2.setType(Material.ICE);
                }
            }
        }
    }
  }
