package ldcr.HopperFiliter;

import org.bukkit.Material;
import org.bukkit.event.EventHandler;
import org.bukkit.event.EventPriority;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryMoveItemEvent;
import org.bukkit.event.inventory.InventoryType;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.plugin.java.JavaPlugin;

public class Main
extends JavaPlugin
implements Listener
{
        public void onEnable()
        {
                getServer().getPluginManager().registerEvents(this, this);
                this.getLogger().info("HopperFiliter漏斗过滤插件 By.Ldcr");
                this.getLogger().info("将一个物品重命名为Filiter并放置在漏斗最后一格使漏斗仅能抽取该类型物品!");
        }

        public void onDisable() {}
                
        @EventHandler
        public void onHopperTransform(InventoryMoveItemEvent e)
        {
        if (e.getInitiator().getType()==InventoryType.HOPPER && e.getDestination()==e.getInitiator())
                {
                        ItemStack transformitem = e.getItem();
                        Inventory destination = e.getDestination();
                        try{
                                if (destination.getItem(4).hasItemMeta())
                                {
                                        if (destination.getItem(4).getItemMeta().getDisplayName().equalsIgnoreCase("Filiter"))
                                        {
                                                Material type= destination.getItem(4).getType();
                                                if (transformitem.getType()!=type)
                                                {
                                                        e.setCancelled(true);
                                                }
                                        }
                                }
                        }
                        catch (NullPointerException exc)
                        {}
                }
        if (e.getItem().hasItemMeta())
        {
                try{
                        if (e.getItem().getItemMeta().getDisplayName().equalsIgnoreCase("Filiter"))
                        {
                                e.setCancelled(true);        
                        }
                }
                catch (NullPointerException exc)
                { }
        }
        }
}复制代码
