                                this.getServer().broadcastMessage("§b服务器娘无情的吞了装备2333,请不要再犯");
                                this.getServer().broadcastMessage("§bW:"+f.getLocation().getWorld().getName()+"x:"+f.getLocation().getX()+"z:"+f.getLocation().getZ());
                                this.getServer().broadcastMessage("§e=======================================");


                                //
                                f.getInventory().setSmelting(null);
                                e.setCancelled(true);
                        }else{        
                        }
                }
        }
        //id判断方法
        boolean pd(int id){
                if(id<=317){
                        if(id>=256){
                                return true;
                        }
                }
                return false;
        }
}
                
                
        
        
