import java.awt.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import javax.swing.*;
public class Win extends JDialog{
   public Win(){
      setLayout(new GridLayout(2,2,50,50));
      Container c=getContentPane();
      JButton j1=new JButton("我是按钮");
      JButton j2=new JButton("劳资是按钮");
      JButton j3=new JButton("本宝宝是按钮");
      JButton j4=new JButton("朕是按钮");
      c.add(j1);
      c.add(j2);
      c.add(j3);
      c.add(j4);
      j1.addActionListener(new ActionListener(){
        public void actionPerformed(ActionEvent e){
           JOptionPane.showMessageDialog(null,"我被点了");
        }
      });
      j2.addActionListener(new ActionListener(){
        public void actionPerformed(ActionEvent e){
           JOptionPane.showMessageDialog(null,"你是个什么东西");
        }
      });
      j3.addActionListener(new ActionListener(){
        public void actionPerformed(ActionEvent e){
           JOptionPane.showMessageDialog(null,"讨厌，点我干嘛");
        }
      });
      j4.addActionListener(new ActionListener(){
        public void actionPerformed(ActionEvent e){
           JOptionPane.showMessageDialog(null,"大胆，敢点朕，拖出去斩了");
        }
      });
      c.add(j1);
      c.add(j2);
      c.add(j3);
      c.add(j4);
      setTitle("神奇的程序_01");
      setSize(960,512);
      setVisible(true);
      setDefaultCloseOperation(WindowConstants.EXIT_ON_CLOSE);
   }    
   public static void main(String[] args) {
   new Win();
   }
}
