@Mod(modid=qulamod.MODID,version=qulamod.VERSION)
public class qulamod {        
        public static final String MODID="qulamod";
        public static final String VERSION="1.0.0";
        public static ItemTool zad=new gz(5, ToolMaterial.EMERALD, null);
        @EventHandler
        public void init(FMLInitializationEvent event){
                GameRegistry.registerItem(zad, "ad");
        }

}
