package dom.qysg.xiami;

import java.util.logging.Logger;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.Server;
import org.bukkit.block.Block;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.block.BlockBreakEvent;
import org.bukkit.plugin.PluginManager;
import org.bukkit.plugin.java.JavaPlugin;

public class lala extends JavaPlugin
  implements Listener
{
  public void onEnable()
  {
    getLogger().info("§btaiyuanle 隔墙挖床修复插件已加载！定制找QQ：1257375501");
    saveDefaultConfig();
    getServer().getPluginManager().registerEvents(this, this);
  }

  @EventHandler
  public void onBlockBreakEvent(BlockBreakEvent e) {
    if (e.getBlock().getType() == Material.BED_BLOCK) {
      Location locblock = e.getBlock().getLocation();
      int blockx = locblock.getBlockX();
      int blocky = locblock.getBlockY();
      int blockz = locblock.getBlockZ();
      Location locplayer = e.getPlayer().getLocation();
      double playerx = locplayer.getX();
      double playery = locplayer.getY();
      double playerz = locplayer.getZ();
      double xx = blockx - playerx; double xxx = xx * xx; double yy = blocky - playery; double yyy = yy * yy; double zz = blockz - playerz; double zzz = zz * zz;
      double sum = xxx + yyy + zzz; double lagsum = Math.sqrt(sum);
      double max = Double.parseDouble(getConfig().getString("max"));
      if (lagsum > max) {
        e.getPlayer().sendMessage(getConfig().getString("Message").replaceAll("&", String.valueOf('§')));
        e.setCancelled(true);
      }
      return;
    }
  }
}
