package com.flansmod.client.model.Zeros_Pack; //Path where the model is located

import com.flansmod.client.model.EnumAnimationType;
import com.flansmod.client.model.ModelGun;
import com.flansmod.common.vector.Vector3f;
import com.flansmod.client.tmt.ModelRendererTurbo;
import com.flansmod.client.tmt.Coord2D;
import com.flansmod.client.tmt.Shape2D;

public class ModelMP40 extends ModelGun //Same as Filename
{
        int textureX = 512;
        int textureY = 512;

        public ModelMP40() //Same as Filename
        {
                gunModel = new ModelRendererTurbo[1];
                gunModel[0] = new ModelRendererTurbo(this, 0, 0, textureX, textureY); // Box 0

                gunModel[0].addObj("guns/mp40.obj"); // Box 0
                gunModel[0].setRotationPoint(0F, 0F, 0F);


                ammoModel = new ModelRendererTurbo[1];
                ammoModel[0] = new ModelRendererTurbo(this, 0, 0, textureX, textureY); // Box 1

                ammoModel[0].addObj("guns/mp40_magazine.obj"); // Box 1
                ammoModel[0].setRotationPoint(0F, 0F, 0F);


                slideModel = new ModelRendererTurbo[1];
                slideModel[0] = new ModelRendererTurbo(this, 0, 0, textureX, textureY); // Box 2

                slideModel[0].addObj("guns/mp40.obj"); // Box 2
                slideModel[0].setRotationPoint(0F, 0F, 0F);



        animationType = EnumAnimationType.RIFLE;



                translateAll(0F, 0F, 0F);


                flipAll();
        }
