我的方法是：@Override
            public void run() 
            {
                for(int i=defender.size()-1;i>=0;i--)
                {
                    Location tmp=defender.get(i);
                    tmp.setY(tmp.getY()+1);
                    Player list[]=Bukkit.getOnlinePlayers();
                    for(int j=0;j<list.length;j++)
                    {
                        Player t=list[j];
                        Location txs=t.getLocation();
                        //getLogger().info(t.getName());
                        int x=tmp.getBlockX(),y=tmp.getBlockY(),z=tmp.getBlockZ();
                        if((list[j].getLocation().getBlockX()-x)*(list[j].getLocation().getBlockX()-x)+(list[j].getLocation().getBlockY()-y)*(list[j].getLocation().getBlockY()-y)+(list[j].getLocation().getBlockZ()-z)*(list[j].getLocation().getBlockZ()-z)<=125) //在一个园内
                        {
                            launch(tmp,txs); //发射
                        }
                            
                    }
                }
            }
