        int jl = new Random().nextInt(100)+1;
                        if(jl>=50){
                                //50至100 劣质品质概率：50/100*100%=50%
                                lore.add("劣质");
                        }
                        else if(jl>=15){
                                //15至50 普通品质概率：35/100*100%=35%
                                lore.add("普通");
                        }
                        else if(jl>=5)
                                //5至15 上好品质概率：10/100*100%=10%
                                lore.add("上好");
                        else if(jl>=0){
                                //0至5 极品品质概率：5/100*100%=5%
                                lore.add("极品");
                        }复制代码

