package com.awt.SkipYouDiedInterface;

import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.plugin.java.JavaPlugin;

import net.minecraft.server.v1_11_R1.PacketPlayInClientCommand;
import net.minecraft.server.v1_11_R1.PacketPlayInClientCommand.EnumClientCommand;
import org.bukkit.craftbukkit.v1_11_R1.entity.CraftPlayer;
import org.bukkit.entity.Player;
import org.bukkit.event.entity.PlayerDeathEvent;
import org.bukkit.event.player.PlayerRespawnEvent;

public class Main extends JavaPlugin implements Listener {
          public void onEnable()
          {
                getServer().getPluginManager().registerEvents(this, this);
            getLogger().info("SkipYouDiedInterface插件已经启动！");
          }
          public void onDisable() {
            getLogger().info("SkipYouDiedInterface插件已经关闭！");
          }
          @EventHandler
          public void onDeath(final PlayerDeathEvent e) {
            getServer().getScheduler().scheduleSyncDelayedTask(this, new Runnable() {
              public void run() {
                ((CraftPlayer) e.getEntity()).getHandle().playerConnection.a(new PacketPlayInClientCommand(EnumClientCommand.PERFORM_RESPAWN));
              }
            });
          }
          @EventHandler
          public void onPlayerReSpawn(PlayerRespawnEvent evt){
                        Player p =evt.getPlayer();
                        p.sendMessage("你死了！已经为您跳过死亡界面！");
                        if (evt.isBedSpawn() == true) { 
                                p.sendMessage("你重生在床记录点。");
                        }
                        else {
                                p.sendMessage("重生信息：XYZ: " + evt.getRespawnLocation().getX() + " / " +evt.getRespawnLocation().getY() + " / " + evt.getRespawnLocation().getZ()  + " 世界：" + evt.getRespawnLocation().getWorld().getName()); 
                        }
                } 
