public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {
    if (cmd.getName().equalsIgnoreCase("mx") && sender instanceof Player) {
      Player player = (Player)sender;
      Location zuobiao = player.getLocation();
      int y = (int)zuobiao.getY();
      int x = (int)zuobiao.getX();
      int z = (int)zuobiao.getZ();

      sender.chat("§a玩家" + sender.getName() + "说：我的坐标：,X:" + x + ",Y:" + y + ",Z:" + z+" ,位于世界: "+zuobiao.getWorld().getName());

      return false;
    }
    return false;
  }
