package payAD;import java.awt.*;
import java.net.*;
import javax.swing.*;
public class Win extends JFrame{
        public Win(){
            Container c=getContentPane();
            JLabel j=new JLabel();
            URL url=Win.class.getResource("/126884.png");
            Icon i=new ImageIcon(url);
            j.setIcon(i);
            j.setHorizontalAlignment(SwingConstants.CENTER);
            j.setOpaque(true);
            c.add(j);
            setTitle("payAD");
            setSize(333,1002);
            setVisible(true);
            setDefaultCloseOperation(WindowConstants.EXIT_ON_CLOSE);
        }
        public static void main(String[] args) {
                new Win();
        }
}
