import java.util.logging.Logger;
import org.bukkit.Bukkit;
import org.bukkit.Server;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.Listener;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.PlayerInventory;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.plugin.Plugin;
import org.bukkit.plugin.PluginManager;
import org.bukkit.plugin.java.JavaPlugin;

public class XBox extends JavaPlugin
{
private thebox[] bb = new thebox[0];
private String msg = "";
cfg_box[] cfg_boxs = new cfg_box[0];

Logger lg = getLogger();

public void onEnable()
{
loadAll();
Listener ls = new Ls(this.bb, this.msg);
getServer().getPluginManager().registerEvents(ls, this);
this.lg.info("##################");
this.lg.info("---XBox加载成功---");
this.lg.info("##################");
}

private void loadAll()
{
try
{
loadCfg(loadYml("config.yml"));

loadTag(loadYml("tag.yml"));
}
catch (Exception e) {
e.printStackTrace();
}
loadBox();
}

private void loadTag(String txt)
{
this.msg = txt;
}

private String loadYml(String FileName)
throws Exception
{
File config = new File(Bukkit.getPluginManager().getPlugin("XBox")
.getDataFolder().getAbsolutePath());
config.mkdirs();
config = new File(Bukkit.getPluginManager().getPlugin("XBox")
.getDataFolder().getAbsolutePath() + 
"\" + FileName);

String testString = getInFile(FileName);
if (config.length() == 0L) {
config.createNewFile();
OutputStreamWriter osw = new OutputStreamWriter(new FileOutputStream(config, true), 
"UTF-8");
osw.write(testString);
osw.flush();
osw.close();
}

InputStreamReader isr = new InputStreamReader(new FileInputStream(config), "UTF-8");
char[] cb = new char[(int)config.length()];
isr.read(cb);
isr.close();
String txt = "";
txt = isString(cb);
txt = txt.replaceAll("&", "§");
String[] txts = txt.split("\\n");
txt = "";
for (int i = 0; i < txts.length; i++) {
if ((txts[i].trim().length() <= 0) || 
(!txts[i].trim().substring(0, 1).equalsIgnoreCase("#")))
{
if (txts[i].trim().length() != 0)
{
if (i == txts.length - 1)
{
txt = txt + txts[i];
}
else txt = txt + txts[i] + "\n";
}
}
}
txt = txt.replaceAll("&", "§");

return txt;
}

void loadCfg(String txt)
{
String[] txts = txt.split("\\[box\\]");
this.cfg_boxs = new cfg_box[0];
for (int i = 0; i < txts.length; i++)
if (txts[i].trim().length() != 0) {
this.cfg_boxs = ((cfg_box[])Arrays.copyOf(this.cfg_boxs, this.cfg_boxs.length + 1));
this.cfg_boxs[(this.cfg_boxs.length - 1)] = loadNewBox(txts[i]);
}
}

private cfg_box loadNewBox(String sub)
{
cfg_box cb = new cfg_box();
String[] subs = sub.trim().split("\\n");
Cmd[] cmd = new Cmd[0];
Random r = new Random();
cb.id = ("box" + r.nextInt(100));
cb.name = "test2";
cb.type = "test3";
cb.item = new String[3];
cb.point = 0;

String now = "";
for (int i = 0; i < subs.length; i++) {
now = subs[i].trim();
if ((now.length() >= 5) && 
(now.substring(0, 5).equalsIgnoreCase("name="))) {
cb.name = now.substring(5);
}
else if ((now.length() >= 3) && 
(now.substring(0, 3).equalsIgnoreCase("id="))) {
cb.id = now.substring(3);
}
else if ((now.length() >= 5) && 
(now.substring(0, 5).equalsIgnoreCase("type="))) {
cb.type = now.substring(5);
}
else if ((now.length() >= 5) && 
(now.substring(0, 5).equalsIgnoreCase("item="))) {
cb.item = now.substring(5).split(",");
}
else if ((now.length() >= 6) && 
(now.substring(0, 6).equalsIgnoreCase("point="))) {
cb.point = Integer.parseInt(now.substring(6));
}

}

subs = sub.trim().split("cmd=");
if (subs.length >= 2) {
subs = subs[1].trim().split("\\}");
if ((subs[0].length() > 1) && 
(subs[0].substring(0, 1).equalsIgnoreCase("{")))
{
if (subs.length >= 1) {
subs = subs[0].trim().split("\\n");
String[] subs2 = new String[0];
for (int i = 0; i < subs.length; i++) {
subs2 = subs[i].split(",");
if (subs2.length >= 4) {
cmd = (Cmd[])Arrays.copyOf(cmd, cmd.length + 1);
boolean call = false;

if (subs2.length >= 5)
{
if (subs2[4].trim().equalsIgnoreCase("true"))
call = true;
if (subs2[4].trim().equalsIgnoreCase("t")) {
call = true;
}
}
if (cb.point >= Integer.parseInt(subs2[2].trim()))
call = true;
cmd[(cmd.length - 1)] = new Cmd(subs2[0], 
subs2[1].replaceAll("_", " "), 
Integer.parseInt(subs2[2].trim()), 
Integer.parseInt(subs2[3].trim()), call);
}

}

cb.cmd = cmd;
}

}

}

return cb;
}

String getInFile(String path)
throws Exception
{
String jfp = XBox.class.getProtectionDomain().getCodeSource()
.getLocation().getFile();
jfp = URLDecoder.decode(jfp, "UTF-8");
jfp = jfp.substring(1, jfp.length());
JarFile jarFile = new JarFile(jfp);
Enumeration et = jarFile.entries();
JarEntry pg = null;
while (et.hasMoreElements()) {
pg = (JarEntry)et.nextElement();
if (pg.getName().equalsIgnoreCase(path)) {
break;
}
}
if (pg.getName().equalsIgnoreCase(path)) {
InputStream pgin = jarFile.getInputStream(pg);

String srtyml = new String("UTF-8");
int bf = 0;
byte[] bfs = new byte[0];
while ((bf = pgin.read()) != -1) {
bfs = Arrays.copyOf(bfs, bfs.length + 1);
bfs[(bfs.length - 1)] = (byte)bf;
}

pgin.close();
srtyml = new String(bfs, "UTF-8");
return srtyml;
}

return "";
}

private String isString(char[] cb)
{
String t = "";
for (int i = 0; i < cb.length; i++) {
t = t + cb[i];
}
return t;
}

public void loadBox() {
this.bb = new thebox[0];
for (int i = 0; i < this.cfg_boxs.length; i++) {
this.bb = ((thebox[])Arrays.copyOf(this.bb, this.bb.length + 1));
thebox s = loadListBox(this.cfg_boxs[i].type, this.cfg_boxs[i].name, 
this.cfg_boxs[i].id, this.cfg_boxs[i].cmd, this.cfg_boxs[i].item);
this.bb[(this.bb.length - 1)] = s;
}
}

private thebox loadListBox(String type, String name, String ID, Cmd[] cmd, String[] item)
{
listBox tlb = new listBox(new ArrayList());
if (cmd != null)
{
for (int i = 0; i < cmd.length; i++) {
tlb.add(cmd[i].name, cmd[i].cmd, cmd[i].point, cmd[i].i, cmd[i].call);
}

}

return new thebox(tlb, name, type, ID, item);
}

public void onDisable()
{
Logger lg = getLogger();

lg.info("##################");
lg.info("---XBox卸载成功---");
lg.info("##################");
}

public boolean onCommand(CommandSender sender, Command cmd, String commandLabel, String[] args)
{
if ((sender instanceof Player))
{
Player p = (Player)sender;
if (!p.hasPermission("xbox"))
{
p.sendMessage("§4您没有执行此指令的权限!");
return true;
}
if (args.length == 0) return false;

if (args[0].equalsIgnoreCase("get"))
{
if (args.length <= 1) return false;

thebox boxs = hasBoxs(this.bb, args[1]);
if (boxs == null) {
p.sendMessage("§c该箱子不存在!");
return false;
}

String[] item = boxs.getItem();

if (item.length >= 2) {
ItemStack im = new ItemStack(Integer.parseInt(item[0]));
ItemMeta itm = im.getItemMeta();
itm.setDisplayName(item[1]);
List lr = new ArrayList();
for (int i = 0; i < item.length - 2; i++) {
lr.add(item[(i + 2)]);
}
lr.add("§1ID: " + boxs.getId());
itm.setLore(lr);
im.setItemMeta(itm);
if (args.length > 2) {
im.setAmount(Integer.parseInt(args[2]));
}
p.getInventory().addItem(new ItemStack[] { im });
}
}
else if (args[0].equalsIgnoreCase("give"))
{
if (args.length <= 2) return false;

thebox boxs = hasBoxs(this.bb, args[2]);
if (boxs == null) {
p.sendMessage("§c该箱子不存在!");
return false;
}

String[] item = boxs.getItem();

if (item.length >= 3) {
ItemStack im = new ItemStack(Integer.parseInt(item[0]));
ItemMeta itm = im.getItemMeta();
itm.setDisplayName(item[1]);
List lr = new ArrayList();
for (int i = 0; i < item.length - 2; i++) {
lr.add(item[(i + 2)]);
}
lr.add("§1ID: " + boxs.getId());
itm.setLore(lr);
im.setItemMeta(itm);
if (args.length > 3) {
im.setAmount(Integer.parseInt(args[3]));
}
Player onlinep = Bukkit.getPlayer(args[1]);
if (onlinep != null) {
onlinep.getInventory().addItem(new ItemStack[] { im });
}
}

}
else if (args[0].equalsIgnoreCase("list")) {
seeList(this.bb, p.getName());
}
else if (args[0].equalsIgnoreCase("help")) {
p.sendMessage("===XBox-Help===");
p.sendMessage("/XBox list 查看所有箱子");
p.sendMessage("/XBox get [箱子ID] <数量> 在手上获得这种箱子");
p.sendMessage("/XBox give [玩家ID] [箱子ID] <数量> 让一个玩家手上获得这种箱子");
p.sendMessage("/XBox help 帮助");
p.sendMessage("/XBox reload 重载配置");
p.sendMessage("===============");
}
else if (args[0].equalsIgnoreCase("reload")) {
loadAll();
p.sendMessage("§6[XBox]配置重载完毕.");
}
else {
return false;
}

return true;
}

if (args.length > 0) {
if (args[0].equalsIgnoreCase("reload"))
{
loadAll();
sender.sendMessage("§6[XBox]配置重载完毕.");
}
else {
sender.sendMessage("该指令仅能由玩家执行。");
}

}

return false;
}

private void seeList(thebox[] bb2, String name)
{
for (int i = 0; i < bb2.length; i++) {
Bukkit.getPlayer(name).sendMessage("|||||||||||");
Bukkit.getPlayer(name).sendMessage("###########");
Bukkit.getPlayer(name).sendMessage("箱子ID:" + bb2[i].getId());
Bukkit.getPlayer(name).sendMessage("箱子名:" + bb2[i].getName());
Bukkit.getPlayer(name).sendMessage("箱子品级:" + bb2[i].getType());
Bukkit.getPlayer(name).sendMessage("奖品列表:");
listBox lb = bb2[i].getBox();
for (int f = 0; f < lb.size(); f++) {
box bx = lb.get(f);
Bukkit.getPlayer(name).sendMessage("物品名称:" + bx.getName() + "|获得数量:" + bx.getI() + "|是否公告:" + lb.get(f).getCall());
}
Bukkit.getPlayer(name).sendMessage("###########");
}
}

private thebox hasBoxs(thebox[] b, String id)
{
for (int i = 0; i < b.length; i++) {
if (b[i].getId().trim().equalsIgnoreCase(id)) {
return b[i];
}

}

return null;
}

class Cmd
{
String name;
String cmd;
int i;
int point;
boolean call;

Cmd(String name, String cmd, int point, int i, boolean call)
{
this.name = name;
this.cmd = cmd;
this.point = point;
this.i = i;
this.call = call;
}
}

class cfg_box
{
String id;
String name;
String type;
XBox.Cmd[] cmd;
String[] item;
int point;

cfg_box()
{
}
}
