package com.mc9ie.Bder;

import java.io.File;
import java.util.ArrayList;
import java.util.List;
import java.util.logging.Logger;
import org.bukkit.Bukkit;
import org.bukkit.Server;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.configuration.file.YamlConfiguration;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.plugin.PluginManager;
import org.bukkit.plugin.java.JavaPlugin;

public class HeroItem extends JavaPlugin
  implements Listener
{
  FileConfiguration config;
  ArrayList<String> name = new ArrayList();

  public void onEnable() { Bukkit.getPluginManager().registerEvents(this, this);
    loadConfig(); }

  @EventHandler
  public void onPlayerJoin(PlayerJoinEvent e) {
    Player p = e.getPlayer();
    Inventory inv = p.getInventory();
    boolean yes = false;
    for (int i = 0; i < inv.getSize(); i++)
      if (inv.getItem(i) != null)
      {
        ItemStack wp = new ItemStack(inv.getItem(i));
        if (wp.hasItemMeta())
        {
          if (wp.getItemMeta().hasDisplayName())
          {
            String name = wp.getItemMeta().getDisplayName();
            if (name.contains(name))
              yes = true; 
          }
        }
      }
    if (yes) {
      for (int i = 0; i < this.config.getStringList("Message").size(); i++) {
        Bukkit.getServer().broadcastMessage(((String)this.config.getStringList("Message").get(i)).replace("&", "§").replace("%player%", p.getName()));
      }
      return;
    }
  }

  public void loadConfig() {
    if (!getDataFolder().exists()) {
      getDataFolder().mkdir();
    }
    File f = new File(getDataFolder(), "config.yml");
    if (!f.exists()) {
      saveDefaultConfig();
    }
    getLogger().info("英雄武器插件加载完成！");
    this.config = YamlConfiguration.loadConfiguration(f);
    for (String i : this.config.getStringList("ItemName"))
      this.name.add(i.replace("&", "§"));
  }
}
根据代码片段，不存在经验提成功能，可以说明楼主根本不了解本插件，并且包名也有所嫌疑，我已经联系了版主，楼主自重
