import org.bukkit.Bukkit;
import org.bukkit.configuration.InvalidConfigurationException;
import org.bukkit.configuration.file.YamlConfiguration;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;

public class InventoryStringDeSerializer {

        public static String InventoryToString(Inventory inv) {
                YamlConfiguration config = new YamlConfiguration();
                config.set("size",inv.getSize());

                for (int i = 0; i < inv.getSize(); i++) {
                        config.set(i+"", inv.getItem(i));
                }
                return config.saveToString();
        }

        public static Inventory StringToInventory(String s) {
                YamlConfiguration config = new YamlConfiguration();
                Inventory inv = null;

                try {
                        config.loadFromString(s);
                        int size = config.getInt("size");
                        inv = Bukkit.createInventory(null, size);
                        for (int i = 0; i < size; i++) {
                                ItemStack is = config.getItemStack(i+"");
                                inv.setItem(i, is);
                        }
                        return inv;
                } catch (InvalidConfigurationException e) {
                        e.printStackTrace();
                }
                return null;
        }
}
