RegExpChat 的核心代码Player player = event.getPlayer();
    boolean pd = false;
    if (!player.hasPermission("regexpchat.bypass"))
    {
      List<String> list = getConfig().getStringList("RegExpList");
      String msg = event.getMessage().toLowerCase();
      for (int x = 0; x < list.toArray().length; x++)
      {
        String regexp = (String)list.toArray()[x];
        regexp = regexp.toLowerCase();
